<style>

    /*      FONTE       */
.link-title{
    font-family: var(--text-font);
    color: var(--icons-color);
    font-weight: bold;
}

.link-text{
    font-family: var(--text-font);
    font-size: 0.75rem;
    color: var(--text-color);
}

    /*      NAV BAR     */
.navbar{
    position: fixed;
    background-color: var(--secondary-color);
    transition: width 200ms ease;
}

.navbar-nav{
    list-style: none;
    padding: 0;
    margin: 0;
    display: flex;
    flex-direction: column;
    align-items: center;
    height: 100%;
}

.nav-item{
    width: 100%;
}

.nav-link{
    display: flex;
    align-items: center;
    height: 5rem;
    color: azure;
    text-decoration: none;
    transition: (--transition-speed);
}

.link-desc{
    display: none;
    margin-left: 1rem;
}

.nav-link svg{
    width: 2rem;
    min-width: 2rem;
    margin: 0 1.5rem;
    color: var(--icons-color);
}

.nav-link img {
    filter: grayscale(1) opacity(0.8);
    width: 3rem;
    margin: 0 1rem;
    transition: var(--transition-speed);
}

.nav-link:hover img {
    filter: grayscale(0%) opacity(1);
}

.navbar:hover{
    /* filter: grayscale(0%) opacity(1); */
    color: var(--icons-color);
}

.svg-color {
    color: var(--icons-color);
    transition: var(--transition-speed);
}

@media only screen and (min-width: 1080px) {
    .navbar{
        top: 0;
        width: 5rem;
        height: 100vh;
    }

    .navbar:hover{
        width: 16rem;
    }

    .navbar:hover .link-desc{
        display: inline;
    }
    
}


@media only screen and (max-width: 1080px) {
    .navbar{
        bottom: 0;
        width: 100vw;
        height: 5rem;
    }

    .navbar-nav{
        flex-direction: row;
    }

    .nav-link{
        justify-content: center;
    }

}

</style>